pragma solidity ^0.8;

// import "./StateVariables.sol";
// import "./Modifiers.sol";
import "./Refund.sol";

abstract contract Contribution is Refund {
    /**
    * @notice contribute to the campaign
    * @dev getting ERC20 token by giving the fiat currency in msg.value
    * @param campaignId helping to find and contribute against the campaign by this id 
    * @param tokens How many token user want's to buy against the fiat currency
    */

    function contribute(uint256 campaignId, uint256 tokens) external payable isCampaignFound(campaignId) isDeadLinePassed(campaignId) returns(bool) {
        Campaign storage currentCampaign = campaigns[campaignId];
        ContributionReceipt storage user = receipts[campaignId][msg.sender];
        require(msg.value == currentCampaign.pricePerToken * tokens, "Incorrect amount");
        require(currentCampaign.status == campainStatus.APPROVED, "Not approved");
        if(currentCampaign.contribution == 0){
        require(
            tokens > 0 &&
            currentCampaign.contribution + tokens <= currentCampaign.target &&
            tokens <= currentCampaign.target,
            "Invalid contribution."
        );
        }
        else
        {
            require(tokens > 0 &&
            currentCampaign.contribution + tokens <= currentCampaign.target,
            "Invalid contributions.");
        }

        currentCampaign.contribution += tokens;

        ERC20Token(currentCampaign.contractAddress).transfer(currentCampaign.creator,msg.sender,tokens * 10**18);

        currentCampaign.heldBalance -= tokens;
        balance[currentCampaign.creator] -= tokens ;
        balance[msg.sender] += tokens;

        user.tokens +=tokens;
        user.receivedEth +=msg.value;
        user.contributer = msg.sender;
        user.campaignId = campaignId;

        emit contribution(campaignId, currentCampaign.contribution, msg.sender);
        return true;

    }
}

