// SPDX-License-Identifier: SEE LICENSE IN LICENSE
pragma solidity^0.8;

import "./ERC20/Ownable.sol";
import "./StateVariables.sol";

abstract contract Modifiers is StateVariables{
    OwnableContract public ownable;

     modifier onlyAdmin(){
        require(
            ownable.checkAdmin(msg.sender)
        );
        _;
    }

     modifier isApprovedOrRejectedAlready(uint256 campaignId) {
        require(campaigns[campaignId].status != campainStatus.APPROVED,"Already approved");
        require(campaigns[campaignId].status != campainStatus.REJECTED,"Already rejected");
        _;
    }

    modifier isCampaignFound(uint256 campaignId) {
        require(campaignCount >= campaignId && campaignId > 0,"No campaign");
        _;
    }

    modifier isDeadLinePassed(uint256 campaignId) {
        require(campaigns[campaignId].endDate >= block.timestamp,"DeadLine passed");
        _;
    }
}
