pragma solidity ^0.8;
// import "./StateVariables.sol";
import "./Modifiers.sol";

abstract contract Refund is Modifiers {
    /**
    * @notice handling the refund
    * @dev users can call refund if they want their contribution back before deadLine
    * @param campaignId campaignId helping to find the campaign by this id 
    * @param _amount amount to be refund
    * 
    */

    function refund(uint256 campaignId, uint256 _amount) external payable isCampaignFound(campaignId) isDeadLinePassed(campaignId) returns (bool) {
        Campaign storage currentCampaign = campaigns[campaignId];
        ContributionReceipt storage user = receipts[campaignId][msg.sender];
        require(user.tokens >= _amount, "Incorrect amount");
        require(address(this).balance >= user.receivedEth, "contract low balance");

        ERC20Token(currentCampaign.contractAddress).transfer(msg.sender,currentCampaign.creator,_amount * 10**18);
        payable(msg.sender).transfer(_amount * currentCampaign.pricePerToken);
        user.tokens -= _amount;
        currentCampaign.contribution -= _amount;

        user.receivedEth -= (_amount) * currentCampaign.pricePerToken;
        currentCampaign.contribution = currentCampaign.contribution;
        currentCampaign.heldBalance += _amount;
        balance[currentCampaign.creator] = currentCampaign.heldBalance;
        balance[msg.sender] -= _amount;
        emit refunded(campaignId, _amount, msg.sender);

        return true;
    }
}

