// SPDX-License-Identifier: MIT
pragma solidity ^0.8.6;
import "./CrowdFunding.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract Dao{
    CrowdFunding instance;
    address public contractAddr;
    string public name;
    string public symbol;
    uint256 _totalSupply;
    // minimum required tokens to creating proposal
    uint8 public toVotePercentage = 10;
    uint8 public equityToVote = 5;
    uint256 minReqTokenCreateProposal;
    uint256 minReqTokenToVoteProposal;

    uint256 public proposalCount;

    mapping(address => uint256) balances;
    mapping(address => mapping(address => uint256)) allowed;


    using SafeMath for uint256;

    constructor(address addr) {
        instance = CrowdFunding(addr);
    }

    modifier isProposalFound(uint256 proposalId, uint256 campaignId) {
        Proposal storage proposal = proposals[campaignId][proposalId];
        require(
            proposalCount >= proposalId && proposalId > 0 && proposal.id == proposalId,
            "invalid id"
        );
        _;
    }

    /// @notice Detailed feilds of proposal
    struct Proposal {
        //  proposal Id
        uint256 id;
        //  owner of the proposal
        address proposer;
        //  favor votes of the proposal
        uint256 favorVotes;
        //  against votes of the proposal
        uint256 againstVotes;
        //  announced = true if the proposal if announced
        bool announced;
        // total No.of voters
        uint256 voterCount;
        // description  of the proposal
        string description;
        // title  of the proposal
        string title;
        // proposal created Date
        uint256 dateOfCreation;
        // deadLine of the proposal for vote
        uint256 deadLine;
        // Contract address of the company
        address proposalCreatedUnder;
        // set the proposal is passed or rejected status by Enum        
        ProposalState res;
    }
    /// @notice receipt record for a voters
    struct VoteReceipt {
        //  Whether Voted or Not
        bool isVoted;
        //  whether vote for favor or against
        bool isSupport;
        //  Vote Count
        uint256 votes;
    }

     struct Vote {
        address voterAddress;
        bool support;
        uint256 votes;
    }

    // The official record of all proposals ever proposed
    // CAMPAIGNID => PROPOSALID => PROPOSAL
    mapping(uint256 =>mapping(uint256=> Proposal)) public proposals;

    // To store receipts of all voters on all proposals
    // proposalId => (voterAddress => VoteReceipt)
    mapping(uint256 => mapping(address => VoteReceipt)) voterReceipts;

    // To store all voters on all proposals
    // proposalId => (voterIndex => voterAddress)
    mapping(uint256 => mapping(uint256 => address)) voters;

    /// @notice Possible states that a proposal may be in
    enum ProposalState {ACTIVE, ACCEPTED, REJECTED}

    /// @notice An event emitted when a new proposal is created
    event ProposalCreated( uint256 id, address proposer, string description, string title);

    /// @notice An event emitted when a vote has been cast on a proposal
    event VoteCast(address voter, uint256 proposalId, bool support);

    function createProposal(
        string memory proposalTitle, 
        string memory proposalDescription, 
        uint256 campaignId, 
        uint256 endDate, 
        address tokenAddress
        ) external{

        (contractAddr, 
        name, 
        symbol, 
        _totalSupply
        ) = instance.companyDetailsByCampaignId(campaignId);
        minReqTokenCreateProposal = (_totalSupply * toVotePercentage / 100);
        minReqTokenToVoteProposal = (_totalSupply * equityToVote / 100); 
        require (ERC20(tokenAddress).balanceOf(msg.sender)>= minReqTokenCreateProposal, "10% stake need");

        proposalCount++;
        Proposal memory newProposal = Proposal({
            id: proposalCount,
            proposer: msg.sender,
            favorVotes: 0,
            againstVotes: 0,
            announced: false,
            voterCount: 0,
            description: proposalDescription,
            title: proposalTitle,
            dateOfCreation: block.timestamp,
            deadLine : endDate,
            proposalCreatedUnder :contractAddr,
            res: ProposalState.ACTIVE
        });

        proposals[campaignId][newProposal.id] = newProposal;
        emit ProposalCreated(
            newProposal.id,
            msg.sender,
            proposalDescription,
            proposalTitle
        );
    }

    function getReceipt(uint256 proposalId, address voter) public view returns (VoteReceipt memory) {
        return voterReceipts[proposalId][voter];
    }

    function bal(address addr) view external returns(uint256){
        return instance.getBalance(addr);
    }

    function stateOfTheProposal(uint256 proposalId, uint256 campaignId) public isProposalFound(proposalId,campaignId) returns (ProposalState) {
        uint256 totalAmountOfPassVoters;
        uint256 totalAmountOfRejectVoters;
        Proposal storage proposal = proposals[campaignId][proposalId];
        if (proposal.announced) {
            for (uint256 i = 0; i < proposal.voterCount; i++) {
                VoteReceipt storage receipt = voterReceipts[proposalId][
                    voters[proposalId][i]
                ];
                if (receipt.isSupport == true) {
                    totalAmountOfPassVoters += balances[voters[proposalId][i]];
                } else if (receipt.isSupport == false) {
                    totalAmountOfRejectVoters += balances[voters[proposalId][i]];
                }
            }
            if (totalAmountOfPassVoters > totalAmountOfRejectVoters) {
                proposal.res = ProposalState.ACCEPTED;
                return ProposalState.ACCEPTED;
            } else {
                proposal.res = ProposalState.REJECTED;
                return ProposalState.REJECTED;
            }
        } else {
            return ProposalState.ACTIVE;
        }
    }

    function voteToProposal(uint256 proposalId, bool vote, uint256 campaignId, address tokenAddress) isProposalFound(proposalId,campaignId) external{
        require(ERC20(tokenAddress).balanceOf(msg.sender) >= minReqTokenToVoteProposal, "5% stake need");
        Proposal storage proposal = proposals[campaignId][proposalId];
        require(
            block.timestamp <= proposal.deadLine,
            "Voting period ended"
        );
        require(
            balances[msg.sender] >= 0,
            "Insufficient token"
        );
        VoteReceipt storage receipt = voterReceipts[proposalId][msg.sender];
        require((receipt.isVoted == false), "vote dublicate");

        receipt.isVoted = true;
        receipt.isSupport = vote;
        receipt.votes = 1;
        voters[proposalId][proposal.voterCount] = msg.sender;
        proposal.voterCount++;
        if (vote) {
            proposal.favorVotes = proposal.favorVotes.add(1);
        } else {
            proposal.againstVotes = proposal.againstVotes.add(1);
        }
        voterReceipts[proposalId][msg.sender] = receipt;
        emit VoteCast(msg.sender, proposalId, vote);
    }

    function proposalResult(uint256 proposalId, uint256 campaignId) external isProposalFound(proposalId,campaignId) {

        VoteReceipt memory _voterReceipt = getReceipt(proposalId, msg.sender);
        require(_voterReceipt.isVoted == true, "Not participate in Ballot" );

        Proposal storage proposal = proposals[campaignId][proposalId];
        require(block.timestamp > proposal.deadLine,"proposalResult: DeadLine has not yet passed");

        ProposalState _ProposalState = stateOfTheProposal(proposalId,campaignId);
        require(
            _ProposalState != ProposalState.ACCEPTED &&_ProposalState != ProposalState.REJECTED,
            "already Declared"
        );
        proposal.announced = true;
    }

    function getVotes(uint256 proposalId, uint256 campaignId) public view returns (Vote[] memory) {
        uint256 voterCount = proposals[campaignId][proposalId].voterCount;
        Vote[] memory votes = new Vote[](voterCount);
        for (uint256 i = 0; i < voterCount; i++) {
            votes[i] = Vote(
                voters[proposalId][i],
                voterReceipts[proposalId][voters[proposalId][i]].isSupport,
                voterReceipts[proposalId][voters[proposalId][i]].votes
            );
        }
        return votes;
    }
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }
}
