// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8;

import "./ERC20.sol";

/// @title CrowdFudndingDao
/// @author
/// @notice You can use this contract for mint a new ERC20 based INRtoken
/// @dev All function calls are currently implemented without side effects

contract States {
    //State Variables
    address public admin;
    ERC20Token private token;

    modifier onlyAdmin() {
        require(admin == msg.sender, "");
        _;
    }

    // modifier isCampaignFound(uint256 campaignId) {
    //     require(campaignCount >= campaignId && campaignId > 0,"No campaign");
    //     _;
    // }

    uint256 public campaignCount;

    struct Campaign {
        // contract address
        address contractAddress;
        // Company Name
        string companyName;
        // Name of the token
        string tokenName;
        // Symbol of the token
        string tokenSymbol;
        // Id of the Campaign
        uint campaignId;
        // Creator of the campaign
        address creator;
        // Title of the campaign
        string campaignTitle;
        // Campaign Description
        string campaignDescription;
        // pricePer token fixed by the user
        uint256 pricePerToken;
        // required fund
        uint256 target;
        // total contribution received
        uint256 contribution;
        // totalHeld Balance
        uint256 heldBalance;
        // Campaign Start Date
        uint256 startDate;
        // Campaign End Date
        uint256 endDate;
        // Status of the campaign
        campainStatus status;
    }

    struct Company {
        // Name of the company
        string companyName;
        // Token symbol created by the company
        string tokenSymbol;
        // Token name created by the company
        string tokenName;
        // Contract address against the company's campaign
        address contractAddress;
        // Total supply
        uint256 totalSupply;
    }

    struct ContributionReceipt {
        // address of the contributer
        address contributer;
        // Id of the camapign
        uint256 campaignId;
        // Token of contribution
        uint256 tokens;
        // Eth value
        uint256 receivedEth;
    }

    /**
     * @notice event for campaign creation
     * @dev This event is emitted when a crowdfunding campaign is made.
     * @param campaignId id of the campaign
     * @param _companyName name of the company going to create the campaign
     * @param _title title of the camapaign
     * @param _description detailed description of the campaign
     * @param _pricePerToken price per token for eg : 10 NEO tokens == 100INR( here 1 neo is equal to 10 INR)
     * @param _tokenName Name of the token launched by the user
     * @param deadLine End date for the campaign to contribute,
     * @param _target goal of the crowd funding
     * @param _tokenSymbol symbol of the token launched by the user
     * @param _totalSupply total supply
     */

    event campaignCreated(
        uint256 indexed campaignId,
        string _companyName,
        string _title,
        string _description,
        uint256 indexed _pricePerToken,
        string _tokenName,
        uint256 indexed deadLine,
        uint256 _target,
        string _tokenSymbol,
        uint256 _totalSupply
    );

    /**
     * @notice approval of the campaign
     * @dev this event is emitted when a campaign gets approve by the admin
     * @param campaignId id of the campaign
     */

    event approved(uint256 indexed campaignId);

    /**
     * @notice rejection of the campaign
     * @dev this event is emitted when a campaign gets reject by the admin
     * @param campaignId id of the campaign
     */

    event rejected(uint256 indexed campaignId);

    /**
     * @notice event for contribution
     * @dev this event will emit when the contribution was maded by the users
     * @param campaignId Id of the campaign
     * @param contribution amount of the contribution by the user
     * @param contributer address of the contributer
     */

    event contribution(
        uint256 indexed campaignId,
        uint256 indexed contribution,
        address indexed contributer
    );

    /**
     * @notice refund request against the contribution
     * @dev This event will emit when refund is made
     * @param campaignId Id of the campaign
     * @param contribution amount of the contribution by the user
     * @param contributer address of the contributer
     */

    event refunded(
        uint256 indexed campaignId,
        uint256 indexed contribution,
        address indexed contributer
    );

    enum campainStatus {
        APPROVED,
        REJECTED,
        PENDING
    }

    // CAMPAIGNID => CAMPAINGS
    mapping(uint256 => Campaign) campaigns;

    //CONTRACT ADDRESS => COMPANY
    mapping(address => Company) companies;

    // CAMPAIGNID => CONTRIBUTER => RECEIPT
    mapping(uint256 => mapping(address => ContributionReceipt)) receipts;

    // USER => BALANCE
    mapping(address => uint256) balance;
}

